  return gulp.src(paths.styles.src) 
    .pipe(mode.development(sourcemaps.init())) 
    .pipe(sass()) 
    .pipe(plumber()) 
    .pipe(autoprefixer({ 
      cascade: false 
    })) 
    .pipe(csscomb()) 
    .pipe(header(banner, { pkg : pkg } )) 
    .pipe(mode.development(sourcemaps.write('.'))) 
